import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { toggleTodo, deleteTodo } from '../store/todos';  // Import toggle and delete actions
import TodoItem from './TodoItem';  // Import TodoItem component
import styled from 'styled-components';  // Import styled-components for styling

// Styled-components for list layout and styling
const List = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

function TodoList() {
  const todos = useSelector((state) => state.todos);  // Access todos from Redux store
  const dispatch = useDispatch();  // Dispatch actions to Redux store

  return (
    <List>
      {todos.map((todo) => (
        <TodoItem
          key={todo.id}
          todo={todo}
          onToggle={() => dispatch(toggleTodo(todo.id))}  // Dispatch toggle action when clicked
          onDelete={() => dispatch(deleteTodo(todo.id))}  // Dispatch delete action when clicked
        />
      ))}
    </List>
  );
}

export default TodoList;
