import React from 'react';
import { Provider } from 'react-redux';
import store from './store';  // Import the Redux store
import TodoForm from './components/TodoForm';  // Import the TodoForm component
import TodoList from './components/TodoList';  // Import the TodoList component
import styled from 'styled-components';  // Import styled-components for styling

// Styled-components for basic layout and styling
const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  text-align: center;
`;

const Title = styled.h1`
  font-size: 2rem;
  margin-bottom: 20px;
`;

function App() {
  return (
    <Provider store={store}>
      <Container>
        <Title>Todo List using Redux</Title>
        <TodoForm />  {/* TodoForm for adding todos */}
        <TodoList />  {/* TodoList for displaying and managing todos */}
      </Container>
    </Provider>
  );
}

export default App;
