import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { toggleStatusTodo, deleteTodo } from '../redux/todos';
import styled from 'styled-components';

const TodoItem = ({ todo }) => {
  const dispatch = useDispatch();

  const toggleStatus = () => dispatch(toggleStatusTodo(todo.id));
  const removeTodo = () => dispatch(deleteTodo(todo.id));

  return (
    <Card>
      <h3>{todo.title}</h3>
      <p>{todo.body}</p>
      <Button onClick={toggleStatus}>
        {todo.isDone ? 'Cancel' : 'Complete'}
      </Button>
      <Button onClick={removeTodo}>Delete</Button>
      <StyledLink to={`/todo/${todo.id}`}>View details</StyledLink>
    </Card>
  );
};

export default TodoItem;

const Card = styled.div`
  border: 1px solid #ccc;
  padding: 20px;
  margin-bottom: 20px;
`;

const Button = styled.button`
  margin-right: 10px;
`;

const StyledLink = styled(Link)`
  color: blue;
`;
