import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addTodo } from '../store/todos';  // Import the addTodo action creator
import styled from 'styled-components';    // Import styled-components for styling

// Styled-components for form layout and styling
const Form = styled.form`
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
`;

const Input = styled.input`
  padding: 10px;
  font-size: 16px;
  width: 300px;
  margin-right: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 10px 20px;
  font-size: 16px;
  background-color: #28a745;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #218838;
  }
`;

function TodoForm() {
  const [input, setInput] = useState('');  // State to track input field
  const dispatch = useDispatch();  // Hook to dispatch actions to the Redux store

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim()) {  // Ensure input is not empty or only spaces
      dispatch(addTodo({ id: Date.now(), text: input, completed: false }));  // Dispatch addTodo action
      setInput('');  // Clear input field after adding todo
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}  // Update input state
        placeholder="Add a todo"
      />
      <Button type="submit">Add</Button>  {/* Submit button */}
    </Form>
  );
}

export default TodoForm;
